import React, { useState } from "react";
import styled from "styled-components";
import { InnerLayout } from "../styles/Layouts";
import Card from "./Card";
import card from "../img/creditcard.svg";
import active from "../img/active.svg";
import inactive from "../img/inactive.svg";
import check from "../img/check.svg";
import checkDisabled from "../img/check-disabled.svg";
import { dataList } from "../data";
import Search from "./Search";

function PaymentSection() {
  const [searchText, setSearchText] = useState("");
  const [data, setData] = useState(dataList);

  const excludeColumns = [
    "id",
    "name",
    "slug",
    "type",
    "price",
    "size",
    "capacity",
    "description",
    "img",
    "location",
  ];
  const handleChange = (value) => {
    setSearchText(value);
    filterData(value);
  };

  // filter records by search text
  const filterData = (value) => {
    const lowercasedValue = value.toLowerCase().trim();
    if (lowercasedValue === "") setData(dataList);
    else {
      const filteredData = dataList.filter((item) => {
        return Object.keys(item).some((key) =>
          excludeColumns.includes(key)
            ? false
            : item[key].toString().toLowerCase().includes(lowercasedValue)
        );
      });
      setData(filteredData);
    }
  };
  return (
    <PaymentStyled>
      <InnerLayout>
        <h3 className="small-heading">
          Find a property <span>at the right price</span>
        </h3>
        <input
          className="input"
          type="text"
          placeholder="Search"
          value={searchText}
          onChange={(e) => handleChange(e.target.value)}
        />
        <div className="card-con">
            <p>vhvh</p>
          {/* <Card
            account={"Free"}
            amount={"$0"}
            text={"Manage your business with a simple and efficient account."}
            button={"Get Started"}
            card={card}
            active={active}
            inactive={inactive}
            check={check}
            checkDis={checkDisabled}
            text1={"10 free local transfers"}
            text2={"Free ATM withdrawals in Dollar up to $250 per month"}
            text3={"Free payments to other Draft accounts"}
            text4={"Prepaid debit cards"}
            text5={"Virtual cards"}
            text6={"Priority 24/7 support"}
            text7={"Exchange 24 currencies"}
            text8={"Multi-user access"}
          />

          <Card
            account={"Premium"}
            amount={"$10"}
            text={"Manage your business with a simple and efficient account."}
            button={"Get Started"}
            card={card}
            active={active}
            inactive={inactive}
            check={check}
            checkDis={checkDisabled}
            text1={"10 free local transfers"}
            text2={"Free ATM withdrawals in Dollar up to $250 per month"}
            text3={"Free payments to other Draft accounts"}
            text4={"Prepaid debit cards"}
            text5={"Virtual cards"}
            text6={"Priority 24/7 support"}
            text7={"Exchange 24 currencies"}
            text8={"Multi-user access"}
          /> */}
          <div className="box-container">
            {data?.map((d, i) => {
              return (
                <div key={i} className="box">
                  {/* <Search value={d}></Search> */}
                  <p>{console.log(data)}</p>
                </div>
              );
            })}
            <div className="clearboth"></div>
            {data?.length === 0 && <span>No records found to display!</span>}
          </div>
        </div>
      </InnerLayout>
    </PaymentStyled>
  );
}

const PaymentStyled = styled.section`
  .card-con {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 3rem;
    padding-top: 7.4rem;
    @media screen and (max-width: 689px) {
      grid-template-columns: repeat(1, 1fr);
    }
  }
  .c-para {
    text-align: center;
  }
`;
export default PaymentSection;
